mapper.update:
UPDATE 
	COUNSEL_INFO
SET
    counsel_writer = #{counsel_writer}
    , counsel_subject = #{counsel_subject}
    , counsel_content = #{counsel_content}
WHERE 
	seq_counsel = #{seq_counsel}

UPDATE 
	COUNSEL_REPLY
SET
	reply_writer = #{reply_writer}
	, reply_content = #{reply_content}
WHERE seq_reply = #{seq_reply}
